				<el-form-item :style='{"width":"45%","margin":"0 2% 20px 0","display":"inline-block"}' v-else-if="ruleForm.xuanjianghuixiangqing" label="宣讲会详情" prop="xuanjianghuixiangqing">
                    <span :style='{"fontSize":"14px","lineHeight":"40px","color":"rgba(90, 67, 74, 1)","fontWeight":"500","display":"inline-block"}' v-html="ruleForm.xuanjianghuixiangqing"></span>
                </el-form-item>
